package com.zhou.common;

import com.alibaba.fastjson2.JSONObject;
import com.zhou.entity.ImageInfo;
import com.zhou.util.GeneralJsonUtil;
import com.zhou.util.OkHttp;
import com.zhou.util.PictureUtils;
import com.zhou.util.YouLaParams;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.data.redis.core.RedisTemplate;
import org.springframework.scheduling.annotation.Async;
import org.springframework.scheduling.annotation.Scheduled;
import org.springframework.stereotype.Component;

import java.io.IOException;
import java.util.Base64;

@Component
@Slf4j
public class ScheduledWork {

    @Autowired
    private RedisTemplate redisTemplate;
    private static ImageInfo imageInfo;

    static {
        String base64Str = "";
        byte[] decode = Base64.getDecoder().decode(base64Str);
        try {
            imageInfo = PictureUtils.readImageByte(decode);
        } catch (IOException e) {
            e.printStackTrace();
            log.error("处理心跳检测fastdeploy图片出现异常,{}", e.getMessage());
        }
    }

    /**
     * 每4分钟心跳检测一次redis，lettuce的历史大问题，太久不用就崩，日你哥
     */
    @Scheduled(cron = "0 0/4 * * * ?")
    public void heartbeatRedis() {
        try {
            redisTemplate.getConnectionFactory().getConnection().ping();
            log.debug("心跳检测成功");
        } catch (Exception e) {
            //System.out.printf("redis没连上");
            log.error("心跳检测失败");
            e.printStackTrace();
        }
    }

    /**
     * 每4分钟连一下fastdeploy，缓解一下第一次慢的问题，顺便把fastjson也给预热了
     */
    @Async
    @Scheduled(cron = "0 0/4 * * * ?")
    public void heartbeatFastdeploy() {
        try {
            long startTime = System.currentTimeMillis();
            JSONObject jsonObject = GeneralJsonUtil.generateJson(imageInfo.getHeight(), imageInfo.getWidth(), imageInfo.getRgbArray());
            OkHttp.fastDeployConnect(jsonObject.toJSONString());
            long endTime = System.currentTimeMillis();
            long elapsedTime = endTime - startTime;
            log.debug("fastdeploy心跳检测成功,顺便代码块执行时间：{}毫秒", elapsedTime);
        } catch (Exception e) {
            //System.out.printf("redis没连上");
            log.error("心跳检测失败");
            e.printStackTrace();
        }
    }

    /**
     * 每30秒保存一次接口调用次数进redis
     */
    @Async
    @Scheduled(cron = "0/30 * * * * ?")
    public void saveUseCounts() {
        try {
            Integer redisNowOcrUserCounts = (Integer) redisTemplate.opsForValue().get(DefineParams.REDIS_OCR_USECOUNTS);
            Integer redisNowFindRoleUserCounts = (Integer) redisTemplate.opsForValue().get(DefineParams.REDIS_FINDROLE_USECOUNTS);
            int ocrUseNowCount = YouLaParams.ocrUseCountsGetValue();
            int roleUseNowCount = YouLaParams.findRoleUseCountsGetValue();
            if (redisNowOcrUserCounts != null && ocrUseNowCount > redisNowOcrUserCounts) {
                redisTemplate.opsForValue().set(DefineParams.REDIS_OCR_USECOUNTS, ocrUseNowCount);
                log.info("例行检查发现使用次数增加，更新使用次数进redis,ocrUseNowCount:{}", ocrUseNowCount);
            }
            if (redisNowFindRoleUserCounts != null && roleUseNowCount > redisNowFindRoleUserCounts) {
                redisTemplate.opsForValue().set(DefineParams.REDIS_FINDROLE_USECOUNTS, roleUseNowCount);
                log.info("例行检查发现使用次数增加，更新使用次数进redis,roleUseNowCount:{}", roleUseNowCount);
            }
            log.debug("例行检查持久化使用次数完毕");
        } catch (Exception e) {
            //System.out.printf("例行检查保存接口调用次数代码异常");
            log.error("例行检查保存接口调用次数代码异常");
            e.printStackTrace();
        }
    }
}
